import React, { createContext, useContext, useEffect, useState } from "react";
import { apiService, ROLES } from "../../services/apiService";
const AuthContext = createContext();

// Make useAuth
export const useAuth = () => useContext(AuthContext);

// Provider

const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on app start
    const initializeAuth = async () => {
      setLoading(true);
      try {
        const { data: user } = await apiService.getCurrentUser();
        setCurrentUser(user);
      } catch (error) {
        console.error('Error initializing auth:', error);
        setCurrentUser(null);
      } finally {
        setLoading(false);
      }
    };

    initializeAuth();
  }, []);


  // User login using email password
  const login = async (email, password) => {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) throw error;

    // Fetch user role
    const { data: dbUser } = await dbService.getCurrentUser();
    const userWithRole = { ...data.user, rol: dbUser?.rol || ROLES.CONSULTA };
    setCurrentUser(userWithRole);
    return { user: userWithRole };
  };

  // User logout
  const logout = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
    setCurrentUser(null);
    return { success: true };
  };


  // forget Password
  const forgetPassword = async (email) => {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: window.location.origin + '/reset-password',
    });
    if (error) throw error;
    return { success: true };
  };


  // Context values
  const value = {
    currentUser,
    logout,
    login,
    forgetPassword,
    loading,
  };
  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
